def row_strategy(history):
    if len(history) < 2:
        # Early game: random choice to avoid predictability
        return random.randint(0, 1)

    # Extract last few moves to detect patterns
    last_fifteen = history[-15:] if len(history) >= 15 else history

    # Track frequency of opponent's moves
    opponent_moves = [move for _, move in last_fifteen]
    opponent_freq = {0: 0, 1: 0}
    for move in opponent_moves:
        opponent_freq[move] += 1

    # If opponent is biased toward 1, we should counter by going 0
    if opponent_freq[1] > opponent_freq[0]:
        # They're likely copying our last move — so if they went 1, we go 0
        if len(history) >= 2:
            our_last_move = history[-1][0]
            if our_last_move == 1:
                return 0
        # Otherwise, use a probabilistic switch based on past patterns
        if random.random() < 0.6:
            return 0
        else:
            return 1
    else:
        # Opponent prefers 0 — we want to coordinate with them
        if len(history) >= 2:
            our_last_move = history[-1][0]
            if our_last_move == 0:
                return 1  # Deviate to 1 to provoke their copy
        # Otherwise, go 0 to match their likely behavior
        if random.random() < 0.4:
            return 0
        else:
            return 1

    # Default fallback
    return random.randint(0, 1)
