def row_strategy(history):
    if len(history) < 3:
        # Not enough history to detect patterns, default to random
        return random.randint(0, 1)

    # Extract last 3 moves (your_move, their_move)
    recent_moves = history[-3:]
    their_moves = [move for _, move in recent_moves]

    # Count frequency of their moves
    count_0 = their_moves.count(0)
    count_1 = their_moves.count(1)

    # If they show strong preference (e.g., 80%+ for one action), shift to the opposite
