import random

def row_strategy(history):
    if not history:
        return 0

    # Count past moves and payoffs
    total_payoff_0 = 0
    total_payoff_1 = 0
    moves = len(history)

    for my_move, their_move in history:
        if my_move == 0:
            if their_move == 0:
                total_payoff_0 += 2
            else:
                total_payoff_0 += 0
        else:
            if their_move == 1:
                total_payoff_1 += 2
            else:
                total_payoff_1 += 0

    # Simple average payoff calculation
    avg_payoff_0 = total_payoff_0 / moves if moves > 0 else 0
    avg_payoff_1 = total_payoff_1 / moves if moves > 0 else 0

    # Prefer action with higher payoff, with exploration
    if avg_payoff_0 > avg_payoff_1:
        return 0
    elif avg_payoff_1 > avg_payoff_0:
        return 1
    else:
        # Equal payoffs: explore with decaying epsilon
        epsilon = 0.1 / (1 + moves / 10)  # Decaying exploration
        return 1 if random.random() < epsilon else 0
