import random
def row_strategy(history):
    if not history:
        return 0

    # Start with 0
    if len(history) == 1:
        return 0

    # Track consecutive opponent copies
    consecutive_copies = 0
    last_opponent_move = history[-1][1]
    second_last_opponent_move = history[-2][1]

    # Check if opponent copied our move in the last two rounds
    if last_opponent_move == history[-1][0] and second_last_opponent_move == history[-2][0]:
        consecutive_copies += 1
    else:
        consecutive_copies = 0

    # If opponent copied twice in a row, switch to 1 with 70% probability
    if consecutive_copies >= 2:
        return 1 if random.random() < 0.7 else 0
    else:
        return 0
