import random
def row_strategy(history):
    if len(history) == 0:
        return 0

    if len(history) == 1:
        return 0

    your_moves = [move for _, move in history]
    their_moves = [move for move, _ in history]

    # Track the last three moves to detect cycles and repetition patterns
    # Use a sliding window of 3 to detect if the opponent is in a repeating pattern
    if len(your_moves) < 3:
        # Early game: default to 0
        return 0

    # Extract last three moves
    last_three_your = your_moves[-3:]
    last_three_their = their_moves[-3:]

    # Detect if opponent is in a repeating cycle (e.g., 0,0,1,1,0,0,1,1)
    # If they repeat every 2 moves, we break the cycle with 10% chance
    def has_cycle(pattern, period=2):
        if len(pattern) < period:
            return False
        for i in range(len(pattern) - period):
            if pattern[i:i+period] == pattern[i+1:i+period+1]:
                return True
        return False

    # If opponent has a 2-period cycle (e.g., 0,0,1,1,0,0), we deviate
    if has_cycle(last_three_their, 2):
        if random.random() < 0.1:
            return 1 - your_moves[-1]
        else:
            return your_moves[-1]

    # If opponent has a 3-period cycle (e.g., 0,1,0,0,1,0), we also deviate
    if has_cycle(last_three_their, 3):
        if random.random() < 0.08:
            return 1 - your_moves[-1]
        else:
            return your_moves[-1]

    # Base deviation logic: if they copied our last move, deviate 20% chance
    last_your_move = your_moves[-1]
    last_their_move = their_moves[-1]

    if last_your_move == last_their_move:
        if random.random() < 0.2:
            return 1 - last_your_move
        else:
            return last_your_move
    else:
        return 0
