    their_cycle = None
    if len(recent_their) >= 2:
        if recent_their[-1] == recent_their[-2]:
            their_cycle = recent_their[-1]

    # If opponent is in a cycle, we break it with 10% chance
    if their_cycle is not None:
        if random.random() < 0.1:
            return 1 - their_cycle
        else:
            return their_cycle

