import random
def row_strategy(history):
    if not history:
        return 0
    # Track the opponent's last move and our own last move
    last_their_move = history[-1][1]
    last_my_move = history[-1][0]

    # If opponent copied our last move (80% chance), we should anticipate this
    # But since they copy with 80% probability, we need to break the cycle
    # Use a simple alternating pattern with noise to avoid predictability

    # Base on parity of round (even/odd) to introduce periodicity
    round_num = len(history)
    if round_num % 2 == 0:
        # Even rounds: switch based on opponent's behavior
        if last_their_move == 1 and last_my_move == 0:
            return 1  # They copied 1, so we go 1 to break pattern
        else:
            return 0
    else:
        # Odd rounds: follow a counter-trend
        if last_their_move == 0 and last_my_move == 1:
            return 0  # They copied 0, we go 0 to maintain balance
        else:
            return 1

    # Add stochasticity to prevent predictability
    if random.random() < 0.1:
        return 1 - last_my_move
    return last_my_move
