import random

class BayesianStrategy:
    def __init__(self):
        self.successes = [1, 1]  # Prior successes for actions 0 and 1
        self.failures = [1, 1]   # Prior failures for actions 0 and 1

    def choose_action(self):
        # Use Thompson Sampling to choose action
        beta_0 = random.betavariate(self.successes[0], self.failures[0])
        beta_1 = random.betavariate(self.successes[1], self.failures[1])
        return 0 if beta_0 > beta_1 else 1

    def update_beliefs(self, my_move, their_move):
        if my_move == their_move:
            self.successes[my_move] += 1
        else:
            self.failures[my_move] += 1

strategy = BayesianStrategy()

def row_strategy(history):
    if history:
        last_move = history[-1]
        strategy.update_beliefs(last_move[0], last_move[1])
    return strategy.choose_action()
ENDOFFILE

