import random

def row_strategy(history):
    if len(history) < 3:
        return random.choices([0, 1], weights=[0.6, 0.4])[0]

    # Extract last 3 moves of the column player
    their_moves = [move for _, move in history[-3:]]

    # If column player chose 1 more than 0 in last 3 rounds, increase chance of choosing 0 (to break pattern)
    if their_moves.count(1) > their_moves.count(0):
        return random.choices([0, 1], weights=[0.7, 0.3])[0]
    else:
        return random.choices([0, 1], weights=[0.3, 0.7])[0]
