import random

def row_strategy(history):
    if len(history) < 2:
        return 0

    # Extract last two opponent moves
    their_moves = [pair[1] for pair in history[-2:]]

    # If opponent cooperated in both last two rounds, increase cooperation probability
    if their_moves == [1, 1]:
        prob_cooperate = 0.7
    else:
        prob_cooperate = 0.5

    if random.random() < prob_cooperate:
        return 0
    else:
        return 1
