import random

def row_strategy(history):
    if len(history) < 3:
        return 0

    # Extract the last three opponent moves
    their_moves = [pair[1] for pair in history[-3:]]

    # Count how many times opponent cooperated (chose 1) in the last three rounds
    cooperation_count = sum(1 for move in their_moves if move == 1)

    # Only cooperate with 0.6 probability if opponent cooperated at least twice
    if cooperation_count >= 2:
        prob_cooperate = 0.6
    else:
        prob_cooperate = 0.4  # Default to lower cooperation probability

    if random.random() < prob_cooperate:
        return 0
    else:
        return 1
