import random

def row_strategy(history):
    if len(history) == 0:
        return 0

    # Initialize payoff tracking (we only care about the row player's payoff)
    # Payoff: 2 if both choose 0, 1 if both choose 1, 0 otherwise
    # So we track cumulative payoff for each action choice

    # Track the action choices and their payoffs
    action_payoffs = {
        0: 0.0,
        1: 0.0
    }

    # We need to compute the payoff for each of our past moves
    for i in range(len(history)):
        my_move, their_move = history[i]
        if my_move == 0 and their_move == 0:
            payoff = 2.0
        elif my_move == 1 and their_move == 1:
            payoff = 1.0
        else:
            payoff = 0.0

        action_payoffs[my_move] += payoff

    # If no history, return 0
    if len(history) == 0:
        return 0

    # Determine which action had higher payoff
    best_action = 0
    best_payoff = action_payoffs[0]
    for action in [1]:
        if action_payoffs[action] > best_payoff:
            best_payoff = action_payoffs[action]
            best_action = action

    # Update probabilities: increase probability of best action by 0.10
    # Cap at 0.75, min at 0.25
    prob = 0.5  # Start with equal probability
    if best_action == 0:
        prob = min(0.75, prob + 0.10)
    else:
        prob = max(0.25, prob + 0.10)

    # Only update if payoff difference exceeds threshold of 0.10
    # Compute difference in payoffs
    diff = abs(action_payoffs[0] - action_payoffs[1])
    if diff > 0.10:
        if best_action == 0:
            prob = min(0.75, prob + 0.10)
        else:
            prob = max(0.25, prob + 0.10)

    # Return 0 with probability 'prob', otherwise 1
    if random.random() < prob:
        return 0
    else:
        return 1
