import random

def row_strategy(history):
    if len(history) < 3:
        # Not enough history to compute a moving average
        return random.randint(0, 1)

    # Extract the last 3 moves of the column player
    their_moves = [entry[1] for entry in history[-3:]]

    # Calculate the proportion of times the column player cooperated (chose 1)
    cooperation_rate = sum(their_moves) / len(their_moves)

    # Dynamic threshold: if >60% cooperation, defect with 70% probability; otherwise, cooperate with 70%
    if cooperation_rate > 0.6:
        return random.choice([1, 0])  # 70% defect (1), 30% cooperate (0)
    else:
        return random.choice([0, 1])  # 70% cooperate (0), 30% defect (1)
