def row_strategy(history):
    if len(history) == 0:
        return 0
    # Weather-based mixed strategy: simulate weather based on round number
    # Sunny (even rounds) → 60% Sports (1), 40% Movie (0)
    # Rainy (odd rounds) → 60% Movie (0), 40% Sports (1)
