import random

def row_strategy(history):
    if len(history) == 0:
        return 0
    # Time-of-day dependent mixed strategy: simulate time of day based on round number
    # Morning (round % 24 < 4 or round % 24 > 16): 60% Sports (1), 40% Movie (0)
    # Evening (round % 24 between 16 and 20): 60% Movie (0), 40% Sports (1)
    round_num = len(history) + 1  # 1-indexed round number
    time_of_day = round_num % 24
    if 16 <= time_of_day <= 20:  # evening hours (6 PM to 10 PM)
        if random.random() < 0.6:
            return 0  # Movie
        else:
            return 1  # Sports
    else:  # morning hours (6 AM to 10 AM) or other times
        if random.random() < 0.6:
            return 1  # Sports
        else:
            return 0  # Movie

ENDOFFILE

