import random

def row_strategy(history):
    if len(history) < 3:
        return 0

    # Extract past moves and compute payoffs
    my_moves = [move for move, _ in history]
    their_moves = [move for _, move in history]

    # Compute payoffs for choosing 0 vs 1 in the past 3 rounds
    payoff_0 = 0
    payoff_1 = 0

    for i in range(len(history) - 2, len(history)):
        if my_moves[i] == 0 and their_moves[i] == 0:
            payoff_0 += 2
        elif my_moves[i] == 1 and their_moves[i] == 1:
            payoff_1 += 2
        elif my_moves[i] == 0 and their_moves[i] == 1:
            payoff_0 += 0
        elif my_moves[i] == 1 and their_moves[i] == 0:
            payoff_0 += 0

    # Simple reinforcement: adjust probability based on payoff
    if payoff_1 > payoff_0:
        prob_fight = min(0.9, 0.5 + 0.4)
    else:
        prob_fight = max(0.1, 0.5 - 0.4)

    return random.choice([0, 1]) if random.random() < prob_fight else 1

