import random

def row_strategy(history):
    if len(history) < 3:
        return 0

    # Reset every 10 rounds to avoid overfitting
    if len(history) % 10 == 0:
        prob_fight = 0.5
    else:
        # Extract last 3 rounds
        last_three = history[-3:]

        # Compute payoffs for choosing 0 vs 1 in the last 3 rounds
        payoff_0 = 0
        payoff_1 = 0

        for my_move, their_move in last_three:
            if my_move == 0 and their_move == 0:
                payoff_0 += 2
            elif my_move == 1 and their_move == 1:
                payoff_1 += 2
            elif my_move == 0 and their_move == 1:
                payoff_0 += 0
            elif my_move == 1 and their_move == 0:
                payoff_0 += 0

        # Adjust probability based on trend
        if payoff_1 > payoff_0:
            prob_fight = min(0.8, 0.5 + 0.15)
        else:
            prob_fight = max(0.2, 0.5 - 0.15)

    return random.choices([0, 1], weights=[1 - prob_fight, prob_fight])[0]

