import random

def row_strategy(history):
    if len(history) < 2:
        return random.choice([0, 1])

    # Extract the last two moves of the column player
    their_moves = [move for _, move in history[-2:]]

    # If column player chose 1 (Fight) in both last two rounds, favor Accord (1)
    if their_moves == [1, 1]:
        return 1
    else:
        return random.choice([0, 1])
