def row_strategy(history):
    if len(history) == 0:
        return 0

    round_num = len(history)

    # Define interval boundaries (every 5 rounds)
    interval = round_num // 5
    current_interval = interval

    # Track opponent's move frequency over last 4 rounds
    if len(history) < 4:
        opponent_freq = 0.0
    else:
        opponent_moves = [move[1] for move in history[-4:]]
        opponent_freq = sum(opponent_moves) / 4.0

    # Threshold for switching to high coordination
    if opponent_freq > 0.6:
        coordination_prob = 0.7
    else:
        coordination_prob = 0.3

    # Adjust based on interval phase (e.g., oscillate between 0.3 and 0.7)
    if current_interval % 2 == 0:
        coordination_prob = 0.3
    else:
        coordination_prob = 0.7

    # Final decision
    if random.random() < coordination_prob:
        return 0
    else:
        return 1
