import random

def row_strategy(history):
    if len(history) < 5:
        # Start with 50-50 mix when not enough data
        return random.choice([0, 1])

    # Extract the last 5 moves of the column player
    recent_their_moves = [move for _, move in history[-5:]]

    # Compute frequency of their choices
    freq_1 = recent_their_moves.count(1)
    freq_0 = recent_their_moves.count(0)

    # Use moving average to detect preference
    if freq_1 > freq_0:
        # They prefer 1 (movie), adjust to 70% probability of choosing 1
        return 1 if random.random() < 0.7 else 0
    elif freq_0 > freq_1:
        # They prefer 0 (restaurant), adjust to 70% probability of choosing 0
        return 0 if random.random() < 0.7 else 1
    else:
        # No clear preference, maintain 50-50
        return random.choice([0, 1])
