def row_strategy(history):
    if len(history) == 0:
        return 0

    # Track opponent's move pattern and our own history
    if len(history) < 2:
        # Early rounds: randomize to avoid predictability
        return random.choice([0, 1])

    # Extract recent moves
    last_two_your_moves = [move for _, move in history[-2:]]
    last_two_their_moves = [move for move, _ in history[-2:]]

    # If opponent copied our last move (80% chance), they're likely to repeat
    # But if they deviated, we exploit that by switching
    if len(last_two_your_moves) == 2 and len(last_two_their_moves) == 2:
        # Check if opponent mirrored our move (same as last)
        if last_two_your_moves[0] == last_two_their_moves[0]:
            # They mirrored, so they're likely to continue mirroring
            # We switch to exploit their preference for 1
            return 1 if last_two_your_moves[1] == 1 else 0
        else:
            # They deviated — possible they're trying to coordinate with partner
            # Since we prefer 0, we go 0 to trigger payoff (2,1)
            return 0

    # Fallback: use a simple alternating pattern with noise
    if len(history) % 3 == 0:
        return 1
    else:
        return 0
