import random

def row_strategy(history):
    if len(history) < 3:
        return random.choices([0, 1], weights=[0.4, 0.6])[0]

    # Extract last 3 opponent moves
    opponent_moves = [move for _, move in history[-3:]]

    # Check if opponent chose 1 (coordination) in two consecutive turns
    consecutive_ones = 0
    for i in range(1, len(opponent_moves)):
        if opponent_moves[i] == 1 and opponent_moves[i-1] == 1:
            consecutive_ones += 1

    if consecutive_ones >= 2:
        return random.choices([0, 1], weights=[0.4, 0.6])[0]  # Adjusted to 0.6 for 1
    else:
        return random.choices([0, 1], weights=[0.4, 0.6])[0]
