def row_strategy(history):
    if len(history) < 2:
        return 0

    # Extract the last two moves of the column player
    their_moves = [move for _, move in history[-2:]]

    # If column player chose 1 (individual) in both last two rounds
    if their_moves[0] == 1 and their_moves[1] == 1:
        return 1  # Respond with 1 to match their coordination

    # If column player chose 0 (coordination) in both last two rounds
