import random

def row_strategy(history):
    if len(history) < 2:
        return 0

    # Initialize belief: Beta(1,1) prior (equal belief in 0 and 1)
    alpha = 1.0  # count of observed 1s
    beta = 1.0   # count of observed 0s

    # Update belief based on opponent's actions
    for _, their_move in history:
        if their_move == 1:
            alpha += 1
        else:
            beta += 1

    # Compute posterior probability that opponent chooses 1
    prob_opponent_1 = alpha / (alpha + beta)

    # Compute expected payoff for each action
    expected_payoff_0 = 2.0 * (1 - prob_opponent_1)  # both choose 0
    expected_payoff_1 = 1.0 * prob_opponent_1       # both choose 1

    # If the opponent is very likely to choose 1 (e.g., >0.8), we switch to 0
    # to exploit the fact that we prefer 0 and get 2 when both pick 0
    if prob_opponent_1 > 0.8:
        return 0
    elif prob_opponent_1 < 0.2:
        return 1
    else:
        # Otherwise, choose based on expected payoff
        if expected_payoff_0 >= expected_payoff_1:
            return 0
        else:
            return 1

