import random

def row_strategy(history):
    if len(history) < 3:
        # Use a simple random choice when history is short
        return random.randint(0, 1)

    # Extract the last 3 opponent moves
    opponent_moves = [move for _, move in history[-3:]]

    # Count how many times opponent chose 0 (Cooperate)
    coop_count = sum(1 for move in opponent_moves if move == 0)

    # Probability of choosing 0 (Cooperate) is proportional to opponent's cooperation
    prob_cooperate = coop_count / 3.0
    prob_defect = 1 - prob_cooperate

    # Apply a small random walk drift: 10% chance to switch direction
    if random.random() < 0.1:
        # Flip the decision with 10% chance
        return 1 - int(prob_cooperate > 0.5)

    # Choose based on weighted probability
    if random.random() < prob_cooperate:
        return 0
