import numpy as np

def row_strategy(history):
    if len(history) < 5:
        return 0

    # Extract column player's moves
    their_moves = [move for _, move in history]

    # Compute average payoff for column player over last 5 rounds
    recent_moves = their_moves[-5:]
    avg_payoff = np.mean(recent_moves)

    # Track threshold using exponential moving average (weight 0.3)
    if len(history) < 10:
        threshold = 0.85
    else:
        # Use exponential moving average of threshold
        # Threshold updates every 10 rounds
        if len(history) % 10 == 0:
            threshold = 0.85
        else:
            threshold = 0.85 * 0.7 + 0.25 * 0.85  # Slow drift

    # If average payoff exceeds threshold, cooperate with 70% chance
    if avg_payoff > threshold:
        return 1 if np.random.random() < 0.7 else 0
    else:
        return 0
ENDOFFILE

