import random
def row_strategy(history):
    if len(history) < 2:
        return 0
    their_moves = [move for _, move in history]
    if len(their_moves) < 2:
        return 0
    # Compute variance of recent moves
    recent_moves = their_moves[-5:]
    if len(recent_moves) < 2:
        return 0
    variance = sum((m - 0.5) ** 2 for m in recent_moves) / len(recent_moves)
    # If high variance, respond randomly; if low, favor 0
    if variance > 0.1:
        return random.choices([0, 1], weights=[0.5, 0.5])[0]
    else:
        return 0
