import random
import math
def row_strategy(history):
    if len(history) < 3:
        return 0
    their_moves = [move for _, move in history]
    if len(their_moves) < 3:
        return 0
    # Compute entropy over last 3 moves
    window = their_moves[-3:]
    if len(window) < 2:
        return 0
    # Count frequencies
    freq = {0: 0, 1: 0}
    for m in window:
        freq[m] += 1
    total = sum(freq.values())
    if total == 0:
        return 0
    probs = [f / total for f in freq.values()]
    # Entropy calculation
    entropy = -sum(p * math.log2(p) for p in probs if p > 0)
    # If high entropy, use mixed strategy
    if entropy > 0.8:
        return random.choices([0, 1], weights=[0.6, 0.4])[0]
    else:
        # Low entropy: copy previous two moves with noise
        if len(window) >= 2:
            prev_two = window[-2:]
            # Copy the second-to-last move with 30% chance of defecting
            if prev_two[0] == prev_two[1]:
                return prev_two[0]
            else:
                return random.choices([prev_two[0], prev_two[1]], weights=[0.7, 0.3])[0]
        return 0
