import random

def row_strategy(history):
    if not history:
        return 0

    # Policy gradient-inspired strategy: learn to match based on opponent patterns
    # Use simple heuristics for now (deep RL not feasible without heavy modeling)

    # If opponent has been consistent (same move for last 2 rounds), match
    if len(history) >= 2:
        last_two_their_moves = [h[1] for h in history[-2:]]
        if last_two_their_moves[0] == last_two_their_moves[1]:
            return last_two_their_moves[0]

    # Otherwise, randomize
    return random.choice([0, 1])

ENDOFFILE

