import random
def row_strategy(history):
    if len(history) == 0:
        return 0
    # Randomly choose to coordinate (same move) with 60% probability
    # Coordinate (both 0 or both 1) gives higher payoff for row (2 when both 0)
    # Misalign (different moves) gives 0 payoff
    if random.random() < 0.6:
        # Coordinate: match their last move to maximize payoff
        if history and history[-1][1] == 0:
            return 0
        else:
            return 1
    else:
        # Misalign: choose opposite of their last move
        if history and history[-1][1] == 0:
            return 1
        else:
            return 0
ENDOFFILE

