import random

def row_strategy(history):
    if len(history) < 2:
        # In early rounds, randomize to avoid predictable patterns
        return random.choice([0, 1])

    # Extract last two moves of the opponent
    last_two_their_moves = [move for _, move in history[-2:]]

    # If opponent repeated the same move in last two rounds, coordinate (choose same)
    if last_two_their_moves[0] == last_two_their_moves[1]:
        # Coordinate with 70% chance (choose same as opponent)
        return last_two_their_moves[0] if random.random() < 0.7 else random.choice([0, 1])
    else:
        # Opponent changed moves — shift to solo move with 70% chance
        # Solo move: if they played 0, row plays 1; if they played 1, row plays 0
        solo_move = 1 - last_two_their_moves[0] if last_two_their_moves[0] == 0 else 1 - last_two_their_moves[1]
        return solo_move if random.random() < 0.7 else random.choice([0, 1])

