import random

def row_strategy(history):
    if len(history) < 4:
        return random.choice([0, 1])

    # Extract column player's move from three rounds ago
    their_move_3_rounds_ago = [move for _, move in history][-4]

    # Current move from the opponent
    _, current_their_move = history[-1]

    # If opponent's move three rounds ago matches current move, coordinate with 70% probability
    if their_move_3_rounds_ago == current_their_move:
        return current_their_move if random.random() < 0.7 else random.choice([0, 1])
    else:
        # Opponent changed — adopt solo move: if they played 0, row plays 1; if 1, row plays 0
        solo_move = 1 - current_their_move
        return solo_move if random.random() < 0.7 else random.choice([0, 1])

