import random

def row_strategy(history):
    if len(history) < 5:
        return random.randint(0, 1)

    # Extract the last 5 moves of the column player
    their_moves = [move for _, move in history[-5:]]

    # Count recent cooperation
    coop_count = sum(1 for move in their_moves if move == 1)

    # Adaptive rule with increasing sensitivity to cooperation
    if coop_count >= 4:
        return 0 if random.random() < 0.99 else 1
    elif coop_count >= 3:
        return 0 if random.random() < 0.90 else 1
    elif coop_count >= 2:
        return 0 if random.random() < 0.80 else 1
    else:
        return 0 if random.random() < 0.60 else 1

ENDOFFILE

