import random

def row_strategy(history):
    if len(history) == 0:
        return 0

    # If we have at least 2 rounds of history to compute state
    if len(history) < 2:
        return 0

    # Extract last two opponent moves
    last_two_moves = [h[1] for h in history[-2:]]

    # Simple rule: if opponent cooperated in both last two rounds, cooperate
    if last_two_moves[0] == 1 and last_two_moves[1] == 1:
        return 1
    elif last_two_moves[0] == 0 and last_two_moves[1] == 0:
        return 0
    else:
        # Random choice
        return 0 if random.random() < 0.5 else 1
ENDOFFILE

