import random
def row_strategy(history):
    # Time-of-day dependent mixed strategy: between 18:00 and 22:00 (evenings), choose movie (0) with 70% probability; otherwise choose sports (1) with 70% probability
    round_num = len(history)
    hour = round_num % 24
    if 18 <= hour < 22:
        return random.choice([0, 0, 0, 0, 0, 0, 1])  # 70% chance of 0 (movie)
    else:
        return random.choice([1, 1, 1, 1, 1, 1, 0])  # 70% chance of 1 (sports)
