import random
def row_strategy(history):
    # Calendar-based mixed strategy: weekends (sat, sun) choose movie (0) with 60% probability; weekdays choose sports (1) with 60% probability
    round_num = len(history)
    day_of_week = round_num % 7
    if day_of_week == 5 or day_of_week == 6:  # Saturday or Sunday (weekend)
        return random.choice([0, 0, 0, 1])  # 60% chance of 0 (movie)
    else:
        return random.choice([1, 1, 1, 0])  # 60% chance of 1 (sports)
