import random

def row_strategy(history):
    if len(history) == 0:
        return 0
    else:
        # Count the frequency of each move by the opponent
        counts = [0, 0]
        for _, move in history:
            counts[move] += 1

        # Calculate the probability of copying the opponent's last move
        total_moves = len(history)
        if total_moves > 0:
            prob_copy = counts[history[-1][1]] / total_moves
        else:
            prob_copy = 0.5

        # Choose the move with a probability of copying or choosing randomly
        if random.random() < prob_copy:
            return history[-1][1]
        else:
            return 0 if random.random() < 0.5 else 1

