def row_strategy(history):
    if not history:
        return 0

    # Analyze opponent's pattern
    opponent_pattern = [move[1] for move in history]
    if len(opponent_pattern) >= 2:
        last_two_moves = opponent_pattern[-2:]
        if last_two_moves.count(0) == 2:
            next_move = 0
        elif last_two_moves.count(1) == 2:
            next_move = 1
