import random

def row_strategy(history):
    if not history:
        return 0  # Initial move

    # Analyze the opponent's previous moves
    opponent_moves = [move[1] for move in history]

    # Calculate the frequency of each move
    move_counts = {0: opponent_moves.count(0), 1: opponent_moves.count(1)}

    # Choose the move that maximizes expected payoff
    if move_counts[0] > move_counts[1]:
        return 0
