import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    my_moves = [move[0] for move in history]
    their_moves = [move[1] for move in history]

    if len(history) < 10:
        return random.choice([0, 1])

    most_common_move = max(set(my_moves), key=my_moves.count)
    if random.random() < 0.8:
        return their_moves[-1]
    else:
        return most_common_move

