import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    population_size = 10
    generations = 100
    mutation_rate = 0.1

    def fitness(individual):
        total_reward = 0
        for my_move, their_move in individual:
            if my_move == their_move:
                total_reward += 2
            elif my_move != their_move:
                total_reward += 0
        return total_reward

    def crossover(parent1, parent2):
        child = []
        for i in range(len(parent1)):
            if random.random() < 0.5:
                child.append(parent1[i])
            else:
                child.append(parent2[i])
        return child

    def mutate(individual):
        for i in range(len(individual)):
            if random.random() < mutation_rate:
                individual[i] = (individual[i][0], random.choice([0, 1]))
        return individual

    population = [[(random.choice([0, 1]), random.choice([0, 1])) for _ in range(10)] for _ in range(population_size)]

    for generation in range(generations):
        population = sorted(population, key=lambda x: fitness(x), reverse=True)[:population_size//2]
        while len(population) < population_size:
            parent1 = random.choice(population)
            parent2 = random.choice(population)
            child = crossover(parent1, parent2)
            child = mutate(child)
            population.append(child)

    best_individual = max(population, key=fitness)
    return best_individual[-1][1]

