import random

def row_strategy(history):
    if len(history) == 0:
        return random.choice([0, 1])

    # Select a random move from history as the initial guess
    last_move = random.choice(history)[-1]

    # With 80% probability, stick with the last move
    if random.random() < 0.8:
        return last_move

    # Otherwise, flip the last move
    return 1 - last_move

