import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    recent_moves = [move[0] for move in history[-10:]]
    most_frequent_move = max(set(recent_moves), key=recent_moves.count)

    if random.random() < 0.2:
        return 1 - most_frequent_move
    else:
        return most_frequent_move

