import random

def row_strategy(history):
    if not history:
        return 0

    my_moves = [move[0] for move in history]
    their_moves = [move[1] for move in history]

    # Genetic algorithm approach
    population_size = 10
    generations = 5
    mutation_rate = 0.1

    def fitness(my_move, their_move):
        if my_move == their_move:
            return 2
        else:
            return 0

    def mutate(move):
        if random.random() < mutation_rate:
            return 1 - move
        else:
            return move

    def crossover(parent1, parent2):
        point = random.randint(0, 1)
        child1 = (parent1[0], parent2[point])
        child2 = (parent2[0], parent1[point])
        return child1, child2

    def evolve(population):
        for _ in range(generations):
            new_population = []
            while len(new_population) < population_size:
                parent1, parent2 = random.sample(population, 2)
                child1, child2 = crossover(parent1, parent2)
                new_population.append((child1, child2))
            population = new_population
        return population

    population = [(random.choice([0, 1]), random.choice([0, 1])) for _ in range(population_size)]

    for individual in population:
        fitness_scores = [fitness(individual[0], move) + fitness(individual[1], move) for move in their_moves]
        best_index = fitness_scores.index(max(fitness_scores))
        individual = (their_moves[best_index], their_moves[best_index])

    best_individual = max(population, key=lambda x: fitness(x[0], their_moves[-1]) + fitness(x[1], their_moves[-1]))
    return best_individual[0]

