import random

Q = {}
alpha = 0.1
gamma = 0.9
epsilon = 0.1

def row_strategy(history):
    global Q

    if not history:
        return random.choice([0, 1])

    last_state = tuple(history[-1])
    current_state = tuple((last_state[1],))

    if current_state not in Q:
        Q[current_state] = {0: 0, 1: 0}

    if random.random() < epsilon:
        next_action = random.choice([0, 1])
    else:
        next_action = max(Q[current_state], key=Q[current_state].get)

    reward = 0
    if last_state == (0, 0):
        reward = 2
    elif last_state == (1, 1):
        reward = 2

    old_value = Q[current_state][next_action]
    next_max = max(Q[(last_state[1],)], default=0)
    new_value = old_value + alpha * (reward + gamma * next_max - old_value)
    Q[current_state][next_action] = new_value

    return next_action

