import random

def row_strategy(history):
    alpha = 0.1
    gamma = 0.9
    epsilon = 0.1
    q_table = {}

    if not history:
        return random.choice([0, 1])

    for state, action in history:
        if (state[0], state[1]) not in q_table:
            q_table[(state[0], state[1])] = [0.0, 0.0]

    current_state = (history[-1][0], history[-1][1])
    if random.random() < epsilon:
        next_action = random.choice([0, 1])
    else:
        next_action = q_table[current_state].index(max(q_table[current_state]))

    reward = 0
    if current_state[0] == current_state[1]:
        if next_action == current_state[0]:
            reward = 2
        elif next_action != current_state[0]:
            reward = 1
    else:
        reward = 0

    old_value = q_table[current_state][next_action]
    next_max = max(q_table[current_state])
    new_value = (1 - alpha) * old_value + alpha * (reward + gamma * next_max)
    q_table[current_state][next_action] = new_value

    return next_action

