import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    # Implement a simple Monte Carlo Tree Search (MCTS) to decide the next move
    num_simulations = 100
    scores = [0, 0]

    for _ in range(num_simulations):
        simulated_history = history[:]
        while len(simulated_history) < 10:
            last_opponent_move = simulated_history[-1][1] if simulated_history else random.choice([0, 1])
            my_move = random.choice([0, 1]) if not simulated_history else last_opponent_move
            simulated_history.append((my_move, last_opponent_move))

        if simulated_history[-1][0] == simulated_history[-1][1]:
            scores[simulated_history[-1][0]] += 1

    return scores.index(max(scores))

