import random

def row_strategy(history):
    if not history:
        return 0  # Initial move
    else:
        # Simulate future outcomes for both strategies
        outcomes = [(0, 1), (1, 0)]
        scores = {move: 0 for move in [0, 1]}

        for _ in range(1000):  # Number of simulations
            my_move = random.choice([0, 1])
            their_move = random.choice(outcomes[my_move])
            scores[my_move] += (2 if my_move == their_move else 0)

        return max(scores, key=scores.get)

