import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    fitness_scores = {}
    for i in range(1000):
        population = [random.choice([0, 1]) for _ in range(len(history))]
        scores = []

        for individual in population:
            score = sum(individual == their_move for _, their_move in history)
            scores.append(score)

        top_individuals = sorted(zip(population, scores), key=lambda x: x[1], reverse=True)[:len(population)//2]
        population = [individual for individual, _ in top_individuals]

        for j in range(len(population)):
            if random.random() < 0.1:
                population[j] = 1 - population[j]

        fitness_scores[i] = sum(individual == their_move for individual, (_, their_move) in zip(population, history))

    best_individual = max(fitness_scores.items(), key=lambda x: x[1])[0]
    return best_individual

