import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    my_last_move = history[-1][0]
    their_last_move = history[-1][1]

    # If the opponent copied my last move, they will likely copy again
    if random.random() < 0.8 and their_last_move == my_last_move:
        return my_last_move

    # Otherwise, switch strategies
    return 1 - my_last_move

